<!DOCTYPE html><html xmlns:fb="http://ogp.me/ns/fb#" xmlns="http://www.w3.org/1999/xhtml" <?php language_attributes(); ?>>    <head  prefix="og:http://ogp.me/ns#">        <meta http-equiv="Content-Type" content="<?php bloginfo('html_type'); ?>; charset=<?php bloginfo('charset'); ?>"/>        <title><?php wp_title( '|' , true , 'right' ); ?></title>        <link rel="profile" href="http://gmpg.org/xfn/11" />        <meta name="viewport" content="width=device-width, initial-scale=1.0">    <?php        get_template_part( 'cfg/templates/head' );        wp_head();     ?>    </head>    <body <?php body_class( ); ?>>        <div class="mythemes-topper">            <a class="mythemes-nav-btn btn-collapse" href="javascript:void(null);" data-effect="mythemes-effect-1">                <span class="mythemes-btn-items">                    <span class="mythemes-btn-item"></span>                    <span class="mythemes-btn-item"></span>                    <span class="mythemes-btn-item"></span>                </span>                <span class="mythemes-label"><?php _e( 'Menu' , 'myThemes' ); ?></span>            </a>        </div>            <div id="mythemes-container" class="mythemes-container">            <div class="mythemes-menu mythemes-effect-1" id="menu-1">                <div class="mythemes-nav-header">                    <h2>                    <a class="brand" href="<?php echo esc_url( home_url( '/' ) ); ?>" title="<?php bloginfo( 'name' ); echo ' '; bloginfo( 'description' ); ?>">                        <?php bloginfo( 'name' ); ?>                    </a>                    </h2>                    <p><?php bloginfo( 'description' ); ?> </p>                    <?php get_template_part( 'searchform' ); ?>                </div>                <nav class="mythemes-nav-menu">                    <?php                        $args = array(                            'theme_location'    => 'header',                            'container_class'   => 'mythemes-nav-wrapper',                            'menu_class'        => 'mythemes-list-menu'                        );                                                $location = get_nav_menu_locations();                        if( isset( $location[ 'header' ] ) && $location[ 'header' ] > 0 ){                            wp_nav_menu( $args );                        }else{                            $pages = get_posts( array(                                'numberposts' => 4,                                'post_type' => 'page',                                'order' => 'ASC'                            ) );                                                        if( !empty( $pages ) ){                                echo '<div class="mythemes-nav-wrapper">';                                echo '<ul class="mythemes-list-menu">';                                foreach( $pages as $p => $item ){                                    $classes = '';                                    if( is_page( $item -> ID ) )                                        $classes = 'current-menu-item';                                                                            echo '<li class="menu-item ' . $classes . '"><a href="' . get_permalink( $item -> ID ) . '">' . $item -> post_title . '</a></li>';                                }                                echo '</ul>';                                echo '</div>';                            }                        }                    ?>                    </nav>            </div>            <div class="mythemes-pusher">                <div class="mythemes-content">                    <div class="mythemes-content-inner">                        <header>                            <?php                                $show_header            = myThemes::get( 'show-header' );                                $show_header_blog       = $show_header && myThemes::get( 'show-header-blog' );                                $show_header_all        = $show_header && myThemes::get( 'show-header-all' );                                $show_header_post       = $show_header && myThemes::get( 'show-header-post' );                                $show_header_page       = $show_header && myThemes::get( 'show-header-page' );                                $is_enb_front_page      = get_option( 'show_on_front' ) == 'page';                                $is_front_page          = $is_enb_front_page && is_front_page();                                if( $is_enb_front_page ){                                    $is_blog_page = is_home();                                }                                else{                                    $is_blog_page = is_front_page();                                }                                $show_header_tmp = false;                                if( $is_front_page && $show_header ){                                    $show_header_tmp = true;                                }                                else if( $is_blog_page && $show_header_blog ){                                    $show_header_tmp = true;                                }                                else if( is_singular( 'post' ) ){                                    $show_header_tmp = $show_header_post ;                                }                                else if( is_singular( 'page' ) ){                                    $show_header_tmp = $show_header_page;                                }                                else if( $show_header_all ){                                    $show_header_tmp = true;                                }                                if( $show_header_tmp ){                                    get_template_part( 'cfg/templates/header' );                                }                            ?>                        </header>                   